    'DisplayName','Actuator Signal');

% Create title
title(titleOfPlot,'FontSize',20);

% Create xlabel
xlabel('Time (s)','FontSize',16);

% Create ylabel
ylabel({'Voltage (V)'},'FontSize',16);

% Create legend
legend(subplot1,'show');

